import React, { useCallback } from "react";
import { Input, message, InputNumber } from 'antd';
import axios from 'axios';
import MD5 from "./md5_lib"
import { REDIS_URL } from "../constants";
import { PASSWORD } from '../constants';
import { SALT } from '../constants';

function InputBox() {

    // Initialize View and Submit Button as disabled: 
    var verify_status;
    var func = function() {       
        // When verify_status == True, button is diabled 
        verify_status = true;
        console.log("verift status initialized as:", verify_status);
        func = function(){};
    }
    func();

    const formItemLayout = {
        labelCol: {
            xs: { span: 24 },
            sm: { span: 8 },
        },
        wrapperCol: {
            xs: { span: 24 },
            sm: { span: 20 },
        },
    };

    const tailFormItemLayout = {
        wrapperCol: {
            xs: {
                span: 16,
                offset: 0,
            },
            sm: {
                span: 20,
                offset: 8,
            },
        },
    };

    // Generate hashcode with salt and password imported from constant.js
    var md5 = new MD5(SALT + PASSWORD);
    const hash = md5.md5;

    // Update sum of scores everytime number is changed
    const UpdateSum = () => {
        // if (localStorage.getItem("verify_status") === 1) {
        //     submitbtn.disabled = false;
        //     viewbtn.disabled = false;
        // }
        // Get sum of scores
        var sum = 0;
        for (var i = 0; i < 5; i++) {
            var score = document.getElementById("git" + i).value;
            if (score === '') {
                score = 0;
            }
            sum += parseInt(score);
        }

        // Set submit-button availability: 
        if (sum > 100 || sum < 0){
            // submitbtn.disabled = true;
            message.error("Total score must be smaller than 100!");
            document.getElementById("sum").value = "Check Score";
        } 
        else {
            // submitbtn.disabled = false;
            document.getElementById("sum").value = sum;

        }
    }


    // Verify that if the user has the authenticate to view and edit the Auction Rank;

    const Verify = async() => {

        const password = document.getElementById("password").value;
        var new_md5 = new MD5(SALT + password);
        const hash_verify = new_md5.md5;

        //interaction with redis backend
        const opt = {
            method: 'GET',
            url: REDIS_URL + '?salt=' + SALT + '&hash=' + hash_verify + '&message=' + 'GET server:name',
            headers: { 'content-type': 'application/json'}
        };

        var res;
        await axios(opt)
            .then( response => {
                console.log('Request sent to Redis: ', opt)
                console.log(response.data)
                if(response.status === 200) {

                    if (!response.data || response.data.length === 0) {
                        message.error('Password Incorrect');
                        verify_status = true;
                    }
                    else {
                        message.success('Verify succeseed, Now you could edit your table!');
                        localStorage.setItem("hash_pass", hash_verify);
                        verify_status = false;
                        // res = response.data;
                    }
                }
            })
            .catch( error => {
                console.log(error.message);
            })


        // console.log("button status:", submitbtn.disabled, viewbtn.disabled);

        // if (res) {
        //     submitbtn.disabled = false;
        //     viewbtn.disabled = false;
        //     // submitbtn.removeAttribute('disabled');
        //     // viewbtn.removeAttribute('disabled');
        // }
        // console.log("button status:", submitbtn.disabled, viewbtn.disabled);
    };


    const Submit = () => {
        // console.log(localStorage.getItem("hash_pass"));
        // console.log("submit verify status:", verify_status);


        if (verify_status == false) {
            var name = document.getElementById("wiki-name");
            const hash = localStorage.getItem("hash_pass");

            if (name.value === '') {
                message.error("Name must not be empty!");
            } 
            else {


                //generate message with scores and wiki-name
                var name = document.getElementById("wiki-name");

                var command = 'HMSET user:' + name.value;

                for (var i = 0; i < 5; i++) {
                    var score = document.getElementById("git" + i).value;
                    if (score === '') {
                        score = 0;
                    }
                    console.log(score);
                    command += ' git' + i + ' ' + score;
                }
                console.log(command)

                //interaction with redis backend
                const opt = {
                    method: 'GET',
                    url: REDIS_URL + '?salt=' + SALT + '&hash=' + hash + '&message=' + command,
                    headers: { 'content-type': 'application/json'}
                };

                axios(opt)
                    .then( response => {
                        console.log('Request sent to Redis: ', opt)
                        console.log(response.data)
                        if(response.status === 200) {
                            if (!response.data || response.data.length === 0) {
                                message.error('Submit Fail');
                            }
                            else {
                                message.success('Submit Succeed');
                            }

                        }
                    })
                    .catch( error => {
                        console.log(error.message);
                        message.error('Submit Fail');
                    })
            }
        } 
        else {
            message.error("Invalid password !");
        }
    };

    // view data function
    // use async..await structure to pass value out from axios
    const ViewData = async() => {

        console.log("submit verify status:", verify_status);

        if (verify_status === false) {

            var name = document.getElementById("wiki-name");

            const hash = localStorage.getItem("hash_pass");

            if (name.value === '') {
                message.error("Name must not be empty!");
            } else {
                //add 1 to flag everytime data is returned successfully
                let flag = 0;
                let sum = 0;

                for (var i = 0; i < 5; i++) {
                    var command = 'HGET user:' + name.value + ' git' + i;
                    const opt = {
                        method: 'GET',
                        url: REDIS_URL + '?salt=' + SALT + '&hash=' + hash + '&message=' + command,
                        headers: { 'content-type': 'application/json'}
                    };
                    let score;
                    await axios(opt)
                        .then( response => {
                            console.log('Request sent to Redis: ', opt)
                            score = response.data.toLocaleString().slice(43)
                            if(response.status === 200) {
                                if (response.data && response.data.length !== 0) {
                                    flag++;
                                }
                            }
                        })
                        .catch( error => {
                            console.log(error.message);
                            message.error('View Request Fail');
                        })
                    console.log(score)
                    document.getElementById("git" + i).value = score;
                    sum += parseInt(score);
                }
                if (flag === 5) {
                    message.success("View Request Succeed")
                    document.getElementById("sum").value = sum;
                }
                else {
                    message.error("View Request Fail")
                }
            }

        }
        else {
            message.error("Invalid password !");
        }

    }


    return (
        <div>             
            <div>
              <Input.Password
                {...formItemLayout}
                placeholder="Your Password" 
                name="password"
                id="password"
                label="Password"
                className="pass-word"
                rules={[
                    {
                        required: true,
                        message: 'Please input your password',
                    },
                ]}/>
            </div>          

            <div>
                <button
                    onClick={Verify}
                    id="verity-btn"
                    className="verify-btn">
                    Verify
                </button>
            </div>
            <div>
                <h1>Grade Auction: </h1>
            </div>

            <div>
                <input
                    type="text"
                    placeholder="Enter Auction's name"
                    id="wiki-name"
                    className="wiki-name"
                />
            </div>
            <div>
                <InputNumber
                    // min={0}
                    // max={100}
                    defaultValue={0}
                    readOnly={true}
                    addonBefore="Sum"
                    id="sum"
                    className="sum"
                />
            </div>
            <div>
                <InputNumber
                    min={0}
                    max={99}
                    defaultValue={0}
                    controls={false}
                    addonBefore="Git0"
                    id="git0"
                    onChange={UpdateSum}
                    className="rank-table"
                />
            </div>
            <div>
                <InputNumber
                    min={0}
                    max={99}
                    defaultValue={0}
                    controls={false}
                    addonBefore="Git1"
                    id="git1"
                    onChange={UpdateSum}
                    className="rank-table"
                />
            </div>
            <div>
                <InputNumber
                    min={0}
                    max={99}
                    defaultValue={0}
                    controls={false}
                    addonBefore="Git2"
                    id="git2"
                    onChange={UpdateSum}
                    className="rank-table"
                    label="123"
                />
            </div>
            <div>
                <InputNumber
                    min={0}
                    max={99}
                    defaultValue={0}
                    controls={false}
                    addonBefore="Git3"
                    id="git3"
                    onChange={UpdateSum}
                    className="rank-table"
                />
            </div>
            <div>
                <InputNumber
                    min={0}
                    max={99}
                    defaultValue={0}
                    controls={false}
                    addonBefore="Git4"
                    id="git4"
                    onChange={UpdateSum}
                    className="rank-table"
                />
            </div>
            <div>
                <button
                    onClick={Submit}
                    // disabled = {true}
                    id="submit-btn"
                    className="submit-btn">
                    Submit
                </button>
            </div>
            <div>
                <button
                    onClick={ViewData}
                    // disabled = {true}
                    id="view-btn"
                    className="view-btn">
                    View
                </button>
            </div>

        </div>


    );
}

export default InputBox;
